import React, { useState } from "react";
import { View, Text, FlatList, Image, StyleSheet, TouchableOpacity, Dimensions } from 'react-native';
const Detalles = ({ route, navigation }) => {
  const { plato } = route.params;
  console.log(plato.image);
  return (
    <View style={{ flex: 1, padding: 16 }}>
     <Image source={{ uri: plato.image }} style={styles.image} />
      <Text style={{ fontSize: 18, fontWeight: "bold" }}>{plato.title}</Text>
      <Text style={{ fontSize: 16, marginTop: 16 }}>{plato.summary}</Text>
      <Button title="Volver" onPress={() => navigation.goBack()} />
    </View>
  );
};
const styles = StyleSheet.create({
  
  image: {
    width: 100,
    height: 100,
    resizeMode: 'cover',
    marginRight: 8,
    borderRadius: 8,
  },

});
export default Detalles;
