import React, { useState } from "react";
import { View, Text, TextInput, Button } from "react-native";

const Detalles = ({ route, navigation }) => {
  const { plato } = route.params;
  console.log(plato.image);
  return (
    <View style={{ flex: 1, padding: 16 }}>
     <Image source={{ uri: plato.image }}/>
      <Text style={{ fontSize: 18, fontWeight: "bold" }}>{plato.title}</Text>
      <Text style={{ fontSize: 16, marginTop: 16 }}>{plato.summary}</Text>
      <Button title="Volver" onPress={() => navigation.goBack()} />
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#f2f2f2',
  },
  menuItem: {
    backgroundColor: 'white',
    borderRadius: 8,
    padding: 16,
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 2,
    elevation: 2,
  },
  itemContent: {
    flexDirection: 'row', // Coloca los elementos en una fila
  },
  image: {
    width: 100,
    height: 100,
    resizeMode: 'cover',
    marginRight: 8,
    borderRadius: 8,
  },
  textContainer: {
    flex: 1,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  summary: {
    fontSize: 16,
    color: '#666',
  },
  moreInfoButton: {
    color: 'blue',
    marginTop: 8,
  },
  EliminarButton: {
    color: 'red',
    marginTop: 8,
  },
});
export default Detalles;
