import React, { useState } from "react";
import { View, Text, FlatList, Image, StyleSheet, TouchableOpacity, Dimensions,Button } from 'react-native';

const Detalles = ({ route, navigation }) => {
  const { plato } = route.params;

  return (
    <View style={{ flex: 1, padding: 16 }}>
      <Image source={{ uri: plato.image.url }} />
      <Text style={{ fontSize: 18, fontWeight: "bold" }}>{plato.title}</Text>
      <Text style={{ fontSize: 16, marginTop: 16 }}>{plato.summary}</Text>

      <Text>Gluten free: {plato.glutenFree ? "Sí" : "No"}</Text>
      <Text>Health score: {plato.healthScore}</Text>
      <Text>Price per serving: ${plato.pricePerServing}</Text>

      <Button title="Volver" onPress={() => navigation.goBack()} />
    </View>
  );
};
const styles = StyleSheet.create({
  
  image: {
    width: 200,
    height: 200,
    resizeMode: 'cover',
    marginRight: 8,
    borderRadius: 8,
  },

});
export default Detalles;
