import React from 'react';
import { View, Text, FlatList, Image, StyleSheet } from 'react-native';

const MenuList = ({ menuData }) => {
  return (
    <View style={styles.container}>
      <FlatList
        data={menuData.results}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.menuItem}>
            <View style={styles.itemContent}>
              <Image source={{ uri: item.image }} style={styles.image} />
              <View style={styles.textContainer}>
                <Text style={styles.title}>{item.title}</Text>
                <Text style={styles.summary}>{item.summary}</Text>
              </View>
            </View>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#f2f2f2',
  },
  menuItem: {

    backgroundColor: 'white',
    borderRadius: 8,
    padding: 16,
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 2,
    elevation: 2,
  },
  itemContent: {
    flexDirection: 'row', // Coloca los elementos en una fila
  },
  image: {
    width: 100, // Ajusta el ancho de la imagen según tus preferencias
    height: 100, // Ajusta la altura de la imagen según tus preferencias
    resizeMode: 'cover',
    marginRight: 8, // Espacio entre la imagen y el texto
    borderRadius: 8,
  },
  textContainer: {
    flex: 1, // Permite que el texto ocupe todo el espacio restante en la fila
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  summary: {
    fontSize: 16,
    color: '#666',
    maxWidth: 30,
  },
});

export default MenuList;
